mapper.select:
SELECT a.accommodation_id AS accomId ,a.title ,a.image ,a.tel ,a.address ,a.mapx ,a.mapy ,a.createdtime ,a.modifiedtime ,a.manager_id AS managerId ,( SELECT COUNT(*) FROM review rw WHERE 1=1 AND rw.parent_Type = 3 AND rw.parent_id = a.accommodation_id ) AS reviewCount FROM accommodation a WHERE 1=1
AND a.title LIKE CONCAT('%',#{searchVo.shValue},'%')
LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}

SELECT COUNT(*) FROM accommodation WHERE 1=1
AND title LIKE CONCAT('%',#{searchVo.shValue},'%')
LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}
