mapper.update:
update forecast set
	avgArtmp = #{avgArtmp}
	,avgWspd = #{avgWspd}
	,avgWtem = #{avgWtem}
	,avgWvhgt = #{avgWvhgt}
	,avgCrsp = #{avgCrsp}
	,weather = #{weather}
	,totalIndex = #{totalIndex}
where 1=1
	and sea_id = #{sea_id}
	and predcNoonSeCd = #{predcNoonSeCd}
	and predcYmd = #{predcYmd}

mapper.select:
SELECT COUNT(*) FROM sea Where 1=1
AND sareaDtlNm LIKE CONCAT('%',#{searchVo.shValue},'%')
LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}

select sea_id ,sareaDtlNm ,lat ,lot from sea where 1=1
AND sareaDtlNm LIKE CONCAT('%',#{searchVo.shValue},'%')
LIMIT #{pageVo.rowNumToShow} OFFSET #{pageVo.startRnumForMysql}
