mapper.update:
update forecast set
	avgArtmp = #{avgArtmp}
	,avgWspd = #{avgWspd}
	,avgWtem = #{avgWtem}
	,avgWvhgt = #{avgWvhgt}
	,avgCrsp = #{avgCrsp}
	,weather = #{weather}
	,totalIndex = #{totalIndex}
where 1=1
	and sea_id =#{sea_id}
	and predcNoonSeCd = #{predcNoonSeCd}
	and predcYmd = #{predcYmd}
